import XCTest
import web3
import BigInt
@testable import swift4337



class PasskeySignerTests: XCTestCase {
    
    let signerPasskey = SafePasskeySigner(publicX: BigUInt("24496937152968818026222377611516525898032551233447781531236978925153890247522"), publicY: BigUInt("4502259600096091720265343022608124325285676227578744180405546100919876529868"), domain: "string")
    
    func testBuildSignatureBytesIsOk() async throws {
        
        let safeSignature = SafeSignature(signer: "0xB3a6Aa3eDDD715dbD84aD465Ab0f98919eBB6d5C", data: "", dynamic: true)
        
        let expected = ""
        
        XCTAssertEqual(SignerUtils.buildSignatureBytes(signatures: [safeSignature]), expected)
    }
    
    func testDummySignatureIsOk() {
        let expected = ""
        
        XCTAssertEqual(try signerPasskey.dummySignature(), expected)
        
        
    }
}
